import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import { Line } from 'react-chartjs-2';

const App = () => {
  const [data1, setData1] = useState(1);
  const [data2, setData2] = useState(1);
  const [data3, setData3] = useState([]);
  const [data4, setData4] = useState([]);
  const [data5, setData5] = useState([]);
  const [showChart1, setShowChart1] = useState(false); // Tila kaavion 1 näyttämiseen/piilottamiseen
  const [showChart2, setShowChart2] = useState(false);
  const [showChart3, setShowChart3] = useState(false);
  const [showChart4, setShowChart4] = useState(false);
  const [showChart5, setShowChart5] = useState(false);



  const toggleChartVisibility = (chartNumber) => {
    switch (chartNumber) {
      case 1:
        setShowChart1(!showChart1);
        break;
      case 2:
        setShowChart2(!showChart2);
        break;
      case 3:
        setShowChart3(!showChart3);
        break;
      case 4:
        setShowChart4(!showChart4);
        break;
      case 5:
        setShowChart5(!showChart5);
        break;
      default:
        break;
    }
  };

  const chartData1 = {
    labels: [...Array(data1.length).keys()],
    datasets: [
      {
        label: 'Data 1',
        data: data1,
        fill: false,
        borderColor: 'rgba(75,192,192,1)',
        tension: 0.1,
      },
    ],
  };

  const chartData2 = {
    labels: [...Array(data2.length).keys()],
    datasets: [
      {
        label: 'Data 2',
        data: data2,
        fill: false,
        borderColor: 'rgba(255,99,132,1)',
        tension: 0.1,
      },
    ],
  };

  const chartData3 = {
    labels: [...Array(data3.length).keys()],
    datasets: [
      {
        label: 'Data 3',
        data: data3,
        fill: false,
        borderColor: 'rgba(54, 162, 235, 1)',
        tension: 0.1,
      },
    ],
  };

  const chartData4 = {
    labels: [...Array(data4.length).keys()],
    datasets: [
      {
        label: 'Data 4',
        data: data4,
        fill: false,
        borderColor: 'rgba(255, 206, 86, 1)',
        tension: 0.1,
      },
    ],
  };

  const chartData5 = {
    labels: [...Array(data5.length).keys()],
    datasets: [
      {
        label: 'Data 5',
        data: data5,
        fill: false,
        borderColor: 'rgba(153, 102, 255, 1)',
        tension: 0.1,
      },
    ],
  };

  return (
    <div>
       <h1>Green House</h1>
      <button onClick={() => toggleChartVisibility(1)}>
        {showChart1 ? 'Piilota Kaavio 1' : 'Näytä Kaavio 1'}
      </button>
      {showChart1 && (
        <div className="chart-container">
          <Line data={chartData1} />
        </div>
      )}
      <button onClick={() => toggleChartVisibility(2)}>
        {showChart2 ? 'Piilota Kaavio 2' : 'Näytä Kaavio 2'}
      </button>
      {showChart2 && (
        <div className="chart-container">
          <Line data={chartData2} />
        </div>
      )}
      <button onClick={() => toggleChartVisibility(3)}>
        {showChart3 ? 'Piilota Kaavio 3' : 'Näytä Kaavio 3'}
      </button>
      {showChart3 && (
        <div className="chart-container">
          <Line data={chartData3} />
        </div>
      )}
      <button onClick={() => toggleChartVisibility(4)}>
        {showChart4 ? 'Piilota Kaavio 4' : 'Näytä Kaavio 4'}
      </button>
      {showChart4 && (
        <div className="chart-container">
          <Line data={chartData4} />
        </div>
      )}
      <button onClick={() => toggleChartVisibility(5)}>
        {showChart5 ? 'Piilota Kaavio 5' : 'Näytä Kaavio 5'}
      </button>
      {showChart5 && (
        <div className="chart-container">
          <Line data={chartData5} />
        </div>
      )}
    </div>
  );
};

export default App;
