import React, { useState } from 'react';
import '../App.css';
import { Line } from 'react-chartjs-2';
import exampleData from '../exampleData'; // Tuodaan esimerkkidata
import { Chart } from "chart.js/auto";

const Charts = () => {
  const [showChart1, setShowChart1] = useState(false);
  const [showChart2, setShowChart2] = useState(false);
  const [showChart3, setShowChart3] = useState(false);
  const [showChart4, setShowChart4] = useState(false);
  const [showChart5, setShowChart5] = useState(false);
  const [showAllCharts, setShowAllCharts] = useState(false);

  return (
    <div>
      <h1>Green House</h1>
      <button onClick={() => setShowChart1(!showChart1)}>
        {showChart1 ? 'Hide Chart 1' : 'Show Chart 1'}
      </button>
      {showChart1 && (
        <div className="chart-container">
          <Line data={exampleData.data1} /> 
        </div>
      )}
      <button onClick={() => setShowChart2(!showChart2)}>
        {showChart2 ? 'Hide Chart 2' : 'Show Chart 2'}
      </button>
      {showChart2 && (
        <div className="chart-container">
          <Line data={exampleData.data2} /> 
        </div>
      )}
      <button onClick={() => setShowChart3(!showChart3)}>
        {showChart3 ? 'Hide Chart 3' : 'Show Chart 3'}
      </button>
      {showChart3 && (
        <div className="chart-container">
          <Line data={exampleData.data3} /> 
        </div>
      )}
      <button onClick={() => setShowChart4(!showChart4)}>
        {showChart4 ? 'Hide Chart 4' : 'Show Chart 4'}
      </button>
      {showChart4 && (
        <div className="chart-container">
          <Line data={exampleData.data4} /> 
        </div>
      )}
      <button onClick={() => setShowChart5(!showChart5)}>
        {showChart5 ? 'Hide Chart 5' : 'Show Chart 5'}
      </button>
      {showChart5 && (
        <div className="chart-container">
          <Line data={exampleData.data5} /> 
        </div>
      )}
      <button onClick={() => setShowAllCharts(!showAllCharts)}>
        {showAllCharts ? 'Hide All Charts' : 'Show All Charts'}
      </button>
      {showAllCharts && (
        <div className="chart-container">
          <Line
            data={{
              labels: exampleData.data1.labels,
              datasets: [
                { ...exampleData.data1.datasets[0], label: 'Data 1' },
                { ...exampleData.data2.datasets[0], label: 'Data 2' },
                { ...exampleData.data3.datasets[0], label: 'Data 3' },
                { ...exampleData.data4.datasets[0], label: 'Data 4' },
                { ...exampleData.data5.datasets[0], label: 'Data 5' },
              ],
            }}
          />
        </div>
      )}
    </div>
  );
  
};

export default Charts;
