import React, { useState } from 'react';
import './App.css';
import { Line } from 'react-chartjs-2';

const App = () => {
  const [showChart1, setShowChart1] = useState(false); // Tila kaavion 1 näyttämiseen/piilottamiseen
  const [showChart2, setShowChart2] = useState(false);
  const [showChart3, setShowChart3] = useState(false);
  const [showChart4, setShowChart4] = useState(false);
  const [showChart5, setShowChart5] = useState(false);

  const exampleData = {
    labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
    datasets: [
      {
        label: 'Data 1',
        data: [65, 59, 80, 81, 56, 55, 40],
        fill: false,
        borderColor: 'rgba(75,192,192,1)',
        tension: 0.1,
      },
    ],
  };

  useEffect(() => {
    return () => {
      setShowChart1(false);
      setShowChart2(false);
      setShowChart3(false);
      setShowChart4(false);
      setShowChart5(false);
    };
  }, []);

  return (
    <div>
       <h1>Green House</h1>
      <button onClick={() => setShowChart1(!showChart1)}>
        {showChart1 ? 'Piilota Kaavio 1' : 'Näytä Kaavio 1'}
      </button>
      {showChart1 && (
        <div className="chart-container">
          <Line data={exampleData} />
        </div>
      )}
      <button onClick={() => setShowChart2(!showChart2)}>
        {showChart2 ? 'Piilota Kaavio 2' : 'Näytä Kaavio 2'}
      </button>
      {showChart2 && (
        <div className="chart-container">
          <Line data={exampleData} />
        </div>
      )}
      <button onClick={() => setShowChart3(!showChart3)}>
        {showChart3 ? 'Piilota Kaavio 3' : 'Näytä Kaavio 3'}
      </button>
      {showChart3 && (
        <div className="chart-container">
          <Line data={exampleData} />
        </div>
      )}
      <button onClick={() => setShowChart4(!showChart4)}>
        {showChart4 ? 'Piilota Kaavio 4' : 'Näytä Kaavio 4'}
      </button>
      {showChart4 && (
        <div className="chart-container">
          <Line data={exampleData} />
        </div>
      )}
      <button onClick={() => setShowChart5(!showChart5)}>
        {showChart5 ? 'Piilota Kaavio 5' : 'Näytä Kaavio 5'}
      </button>
      {showChart5 && (
        <div className="chart-container">
          <Line data={exampleData} />
        </div>
      )}
    </div>
  );
};

export default App;
