import React, { useState, useEffect } from 'react';

const WeatherForecast = () => {
  const [forecastData, setForecastData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          'http://api.openweathermap.org/data/2.5/forecast?id=524901&appid={dd8e3a1525d8c554ba445de6cd6460cd}&units=metric' // Lisää API-avaimesi tähän
        );
        const data = await response.json();
        setForecastData(data);
      } catch (error) {
        console.error('Error fetching weather forecast:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h2>Weather Forecast</h2>
      {forecastData ? (
        <div>
          <p>City: {forecastData.city.name}</p>
          <p>Country: {forecastData.city.country}</p>
          <h3>Forecast:</h3>
          <ul>
            {forecastData.list.map((item, index) => (
              <li key={index}>
                {item.dt_txt}: {item.main.temp} °C
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default WeatherForecast;
